mapper.update:
update jdbc_board set board_title = #{board_title}, 
board_content = #{board_content}, board_date = sysdate 
where board_no = #{board_no}

mapper.select:
select board_no, board_title, board_writer,
to_char(board_date, 'YYYY-MM-DD') board_date, board_cnt,
board_content from jdbc_board where board_no = #{board_no}
